%% Unescaping
unescape([$\\, C | Tail]) ->
	%% unescaping
	[C | unescape(Tail)];
unescape([$" | Tail]) ->
	%% stripping quotes (only possible at start and end)
	unescape(Tail);
unescape([C | Tail]) ->
	[C | unescape(Tail)];
unescape([]) -> [].


value_of(Token) ->
    try element(3, Token)
    catch error:badarg ->
            error({badarg, Token})
    end.

